using System;
using System.Net.Http;
using System.Net.Http.Headers;
using System.Text;
using System.Threading.Tasks;

namespace EMQ.API.Examples
{
  class API
  {
    private const string SERVER_URL = "https://sandbox-partner.emq.com";
    private const string USERNAME = "FILL_IN_USERNAME";
    private const string PASSWORD = "FILL_IN_PASSWORD";
    static async Task Main(string[] args)
    {
      HttpClient client = new HttpClient();
      client.BaseAddress = new Uri(SERVER_URL);
      // Put the auth information to the header and use it make the request in
      // the future.
      client.DefaultRequestHeaders.Authorization =
        new AuthenticationHeaderValue(
          "Basic",
          System.Convert.ToBase64String(
            UTF8Encoding.UTF8.GetBytes($"{USERNAME}:{PASSWORD}")));

      // make request
      HttpResponseMessage result = await client.GetAsync(
        $"/v1/data/countries");
      string jsonResult = await result.Content.ReadAsStringAsync();
      if (result.IsSuccessStatusCode)
      {
        Console.WriteLine($"Request result {jsonResult}");
      }
      else
      {
        Console.WriteLine($"error: {result.StatusCode}\n{jsonResult}");
      }
    }
  }
}

package com.emq.api.example;

import java.net.URI;
import java.net.http.HttpClient;
import java.net.http.HttpRequest;
import java.net.http.HttpResponse;
import java.util.Arrays;
import java.util.Base64;
import java.util.List;

public class Main {
  private final static String BASE_URL = "https://sandbox-partner.emq.com/v1";
  private final static String USERNAME = "FILL_IN_USERNAME";
  private final static String PASSWORD = "FILL_IN_PASSWORD";
  private final static List<Integer> SUCCESS_STATUS_CODE = Arrays.asList(200, 201, 202);

  public static void main(String[] args) {
    try {
      byte[] authBytes = (USERNAME + ":" + PASSWORD).getBytes();
      String authToken = "Basic " + Base64.getEncoder().encodeToString(authBytes);

      String apiPath = "/data/countries";

      HttpClient client = HttpClient.newBuilder()
          .followRedirects(HttpClient.Redirect.NORMAL)
          .build();
      HttpRequest request = HttpRequest.newBuilder()
          .uri(new URI(BASE_URL + apiPath))
          .header("Authorization", authToken)
          .GET()
          .build();
      HttpResponse<String> response = client.send(request,
          HttpResponse.BodyHandlers.ofString());
      if (SUCCESS_STATUS_CODE.contains(response.statusCode())) {
        System.out.println("Request successfully:\n" + response.body());
      } else {
        System.out.println("Request failed with code " + response.statusCode()
            + ":\n" + response.body());
      }
    } catch (Exception ex) {
      System.out.println(ex);
      System.exit(-1);
    }
  }
}

